mapper.delete:
UPDATE formpage
deleteName =#{deleteName}, deleteTime =#{deleteTime}, curStatus=0
WHERE formcmName=#{formcmName}

mapper.update:
UPDATE formpage
formcmTitle =#{formcmTitle}, formTask =#{formTask}, listTask =#{listTask}, editPage =#{editPage}, pageRelease =#{pageRelease}, modifyName =#{modifyName}, modifyTime =#{modifyTime}, module =#{module}
where formcmName=#{formcmName}

mapper.select:
SELECT * FROM formpage
and formcmName like concat('%', #{formcmName}, '%') and formcmTitle like concat('%', #{formcmTitle}, '%') and formTask like concat('%', #{formTask}, '%') and listTask like concat('%', #{listTask}, '%') and module =#{module} and recordName like concat('%', #{recordName}, '%') and modifyName like concat('%', #{modifyName}, '%') AND formType = #{formType} AND (formType = 1 OR formType = 3) AND curStatus=2
order by recordTime desc limit #{startRow},#{endRow}

SELECT * FROM formpage

    and formcmName =#{formcmName}
    and formcmTitle =#{formcmTitle}
    and formTask =#{formTask}
    and listTask =#{listTask}
    and recordName =#{recordName}
    and modifyName =#{modifyName}
    AND formType = #{formType}
    AND (formType = 1 OR formType = 3) 
    AND curStatus=2

order by recordTime desc

SELECT * FROM formpage WHERE curStatus=2

    AND formcmName IN

        #{formcmName}
