mapper.update:
UPDATE MyUrlRegist
parentId =#{parentId}, pageTitle =#{pageTitle}, menuNum =#{menuNum}, formId =#{formId}, module =#{module}, formType =#{formType}, modifyName =#{modifyName}, modifyTime =#{modifyTime}
where pageId=#{pageId} and pageid not in ('topmenu')

mapper.select:
SELECT pageId FROM MyUrlRegist

    and parentId = #{parentId}
    and pageTitle like concat('%', #{pageTitle}, '%')
    and formId = #{formId}
    and menuNum = #{menuNum}
    and module like concat('%', #{module}, '%')
    and formType =#{formType}
    and pageid not in ('topmenu') and curStatus=2

order by security asc

SELECT * FROM MyUrlRegist

    and pageId = #{pageId}
    and parentId = #{parentId}
    and pageTitle like concat('%', #{pageTitle}, '%')
    and formId = #{formId}
    and menuNum = #{menuNum}
    and module like concat('%', #{module}, '%')
    and formType =#{formType}
    and formId not in(${formIds}) and formId != ''
    and pageid not in ('topmenu') and curStatus=2

order by security asc

SELECT count(*) FROM MyUrlRegist

    and pageId = #{pageId}
    and parentId = #{parentId}
    and pageTitle like concat('%', #{pageTitle}, '%')
    and formId = #{formId}
    and menuNum = #{menuNum}
    and module like concat('%', #{module}, '%')
    and formType =#{formType}
    and pageid not in ('topmenu') and curStatus=2
