mapper.delete:
UPDATE WorkFlowProc
deleteName =#{deleteName}, deleteTime =#{deleteTime}, curStatus=0
WHERE procId=#{procId} AND wkflwID =#{wkflwID}

UPDATE WorkFlowProc

    curStatus=0

WHERE procId in (${procIds})  AND wkflwID =#{wkflwId}

mapper.update:
UPDATE WorkFlowProc
startNodeID =#{startNodeID}, originator =#{originator}, flowpeoples =#{flowpeoples}, nodespeople =#{nodespeople}, wkfStatus =#{wkfStatus}, curNodeID =#{curNodeID}, wkfResult =#{wkfResult}, endNodeID =#{endNodeID}, security =#{security}, runLevel =#{runLevel},
modifyName =#{modifyName}, modifyTime =#{modifyTime}
where curStatus=2 AND procId=#{procId} AND wkflwID =#{wkflwID}

mapper.select:
SELECT * FROM WorkFlowProc

    and procId =#{procId}
    and wkflwID =#{wkflwID}
    and startNodeID =#{startNodeID}
    and originator =#{originator}
    and flowpeoples like CONCAT('%',#{flowpeoples},';','%')
    and curNodeID =#{curNodeID}
    and wkfResult =#{wkfResult}
    and wkfStatus =#{wkfStatus}
    and endNodeID =#{endNodeID}
    and security =#{security}
    and runLevel =#{runLevel}
    or (nodespeople like CONCAT('%',#{nodespeople},';','%') and wkfStatus =#{wkfStatus})
    and curStatus=2

order by recordTime desc limit ${startRow},${endRow}

SELECT count(*) FROM WorkFlowProc

    and procId =#{procId}
    and wkflwID =#{wkflwID}
    and startNodeID =#{startNodeID}
    and originator =#{originator}
    and flowpeoples like CONCAT('%',#{flowpeoples},';','%')
    and curNodeID =#{curNodeID}
    and wkfResult =#{wkfResult}
    and endNodeID =#{endNodeID}
    and security =#{security}
    and wkfStatus =#{wkfStatus}
    or ( nodespeople like CONCAT('%',#{nodespeople},';','%') and wkfStatus =#{wkfStatus} )
    and runLevel =#{runLevel}
    and curStatus=2

order by recordTime desc

SELECT IFNULL(COUNT(wkfStatus),0) AS COUNT FROM WorkFlowProc WHERE wkflwId = #{wkflwId}
AND originator =#{originator}
AND wkfStatus = 1
AND flowpeoples like CONCAT('%',#{originator},';','%') OR ( nodespeople like CONCAT('%',#{originator},';','%') and wkfStatus = 1 )
AND curStatus = 2

UNION ALL
SELECT IFNULL(COUNT(wkfStatus),0) AS COUNT FROM WorkFlowProc WHERE wkflwId = #{wkflwId}
AND originator =#{originator}
AND wkfStatus = 2
AND flowpeoples like CONCAT('%',#{originator},';','%') OR ( nodespeople like CONCAT('%',#{originator},';','%') and wkfStatus = 2 AND curStatus = 2)
AND curStatus = 2
