mapper.update:
UPDATE employees
staffName =#{staffName}, sex =#{sex}, dateOfBirth =#{dateOfBirth}, age =#{age}, nations =#{nations}, married =#{married}, nativePlace =#{nativePlace}, address =#{address}, phone =#{phone}, photo =#{photo}, mailbox =#{mailbox}, remark =#{remark}, department =#{department}, post =#{post}, hiredate =#{hiredate}, contractsExpire =#{contractsExpire}, leaveDate =#{leaveDate}, userName =#{userName}, modifyName =#{modifyName}, modifyTime =#{modifyTime},
WHERE curStatus=2 AND staffId=#{staffId};

mapper.select:
SELECT * FROM usermanager WHERE curStatus=2

    AND staffId IN

        #{userName}


ORDER BY recordName ASC,sort asc
 limit #{startRow},#{endRow}

SELECT * FROM employees

    AND staffName =#{staffName}
    AND sex =#{sex},
    AND dateOfBirth =#{dateOfBirth}
    age =#{age}
    AND nations =#{nations}
    AND married =#{married}
    AND nativePlace =#{nativePlace}
    AND address =#{address}
    AND phone =#{phone}
    AND mailbox =#{mailbox}
    AND remark =#{remark}
    AND department =#{department}
    AND post =#{post}
    AND hiredate =#{hiredate},
    AND contractsExpire =#{contractsExpire}
    AND leaveDate =#{leaveDate}
    AND userName =#{userName}
    and curStatus=2

ORDER BY recordName ASC,sort asc
 limit #{startRow},#{endRow}

