mapper.update:
UPDATE Message
msgTitle =#{msgTitle}, msgSendUser =#{msgSendUser}, msgRecUser =#{msgRecUser}, msgText =#{msgText}, msgType =#{msgType}, msgUrl =#{msgUrl}, msgLevel =#{msgLevel}, msgSendTime =#{msgSendTime}, msgEndTime =#{msgEndTime}, msgStatus =#{msgStatus},
WHERE curStatus=2 AND msgId=#{msgId}

mapper.select:
SELECT * FROM Message

    and msgId =#{msgId}
    and msgTitle =#{msgTitle}
    and msgSendUser =#{msgSendUser}
    and msgRecUser =#{msgRecUser}
    and msgText =#{msgText}
    and msgUrl =#{msgUrl}
    and msgSendTime =#{msgSendTime}
    and msgEndTime =#{msgEndTime}
    and msgStatus =#{msgStatus}
    and curStatus=2

ORDER BY msgLevel DESC, msgSendTime ASC

SELECT * FROM Message

    and msgId =#{msgId}
    and msgTitle like concat('%', #{msgTitle}, '%')
    and msgSendUser =#{msgSendUser}
    and msgRecUser =#{msgRecUser}
    and msgText =#{msgText}
    and msgUrl =#{msgUrl}
    and msgSendTime =#{msgSendTime}
    and msgEndTime =#{msgEndTime}
    and msgStatus =#{msgStatus}
    and curStatus=2

ORDER BY msgLevel DESC, msgSendTime ASC

SELECT COUNT(*) FROM Message

    and msgId =#{msgId}
    and msgTitle like concat('%', #{msgTitle}, '%')
    and msgSendUser =#{msgSendUser}
    and msgRecUser =#{msgRecUser}
    and msgText =#{msgText}
    and msgUrl =#{msgUrl}
    and msgSendTime =#{msgSendTime}
    and msgEndTime =#{msgEndTime}
    and msgStatus =#{msgStatus}
    AND curStatus=2
