mapper.delete:
UPDATE AccessRights
pageId =#{pageId}, roleId =#{roleId}, accessType =#{accessType}, deleteName =#{deleteName}, deleteTime =#{deleteTime}, curStatus=0
WHERE accessId=#{accessId}

UPDATE AccessRights
deleteName =#{deleteName}, deleteTime =#{deleteTime}, curStatus=0
WHERE pageId=#{pageId}

mapper.update:
UPDATE AccessRights
pageId =#{pageId}, roleId =#{roleId}, accessType =#{accessType}, modifyName =#{modifyName}, modifyTime =#{modifyTime}
where accessId=#{accessId}
