mapper.delete:
UPDATE RoleDefines
deleteName =#{deleteName}, deleteTime =#{deleteTime}, curStatus=0
WHERE roleId=#{roleId}

mapper.update:
UPDATE RoleDefines
roleName =#{roleName}, roleTitle =#{roleTitle}, userName =#{userName}, module =#{module}, modifyName =#{modifyName}, modifyTime =#{modifyTime}
where roleId=#{roleId}
