mapper.delete:
UPDATE UserManager
modifyName = #{deleteName}, deleteTime = #{deleteTime}, curStatus=0
WHERE userName = #{userName}

mapper.update:
UPDATE UserManager
name =#{name}, password =#{password}, computerIP =#{computerIP}, staffId =#{staffId}, accountStatus =#{accountStatus}, userStatus =#{userStatus}, startDate =#{startDate}, endDate =#{endDate}, modifyName =#{modifyName}, modifyTime =#{modifyTime}
where userName=#{userName} and curStatus=2

mapper.select:
SELECT * FROM UserManager WHERE CURSTATUS=2 order by id desc

    and userName=#{userName}
    and name =#{name}
    and password =#{password}
    and computerIP =#{computerIP}
    and staffId =#{staffId}
    and accountStatus =#{accountStatus}
    and userStatus =#{userStatus}
    and startDate =#{startDate}
    and endDate =#{endDate}
    AND curStatus=2

order by recordTime desc
 limit #{startRow},#{endRow}

SELECT * FROM UserManager

    and userName=#{userName}
    and name like concat('%', #{name}, '%')
    and password =#{password}
    and computerIP =#{computerIP}
    and staffId =#{staffId}
    and accountStatus =#{accountStatus}
    and userStatus =#{userStatus}
    and startDate =#{startDate}
    and endDate =#{endDate}
    AND curStatus=2

order by recordTime desc
 limit #{startRow},#{endRow}

select count(*) from USERMANAGER

    and userName=#{userName}
    and name like concat('%', #{name}, '%')
    and password =#{password}
    and computerIP =#{computerIP}
    and staffId =#{staffId}
    and accountStatus =#{accountStatus}
    and userStatus =#{userStatus}
    and startDate =#{startDate}
    and endDate =#{endDate}
    AND curStatus=2

select * from userinfo

       OR userName=#{userName}
       OR (staffName=#{staffName} OR staffName like concat('%', #{staffName}, '%'))
       OR deptId=#{deptId}
       OR (deptName=#{deptName} OR deptName like concat('%', #{deptName}, '%'))

   limit #{startRow},#{endRow}

select count(*) from userinfo

     OR userName=#{userName}
     OR (staffName=#{staffName} OR staffName like concat('%', #{staffName}, '%'))
     OR deptId=#{deptId}
     OR (deptName=#{deptName} OR deptName like concat('%', #{deptName}, '%'))

select * from userinfo


        deptId in (select deptId from  department WHERE FIND_IN_SET(deptId,getChildrenOfDept(#{deptId})) AND deptId not in ('organize') AND curStatus=2)


 limit #{startRow},#{endRow}
