mapper.delete:
UPDATE WorkFlowDefine
deleteName =#{deleteName}, deleteTime =#{deleteTime}, curStatus=0
WHERE wkflwID=#{wkflwID}

mapper.update:
UPDATE WorkFlowDefine
wkfName =#{wkfName}, wkfType =#{wkfType}, tableName =#{tableName}, wkfValue =#{wkfValue}, mailContent =#{mailContent}, specialField =#{specialField}, pageId =#{pageId}, flowLabFld =#{flowLabFld}, pkFeilds =#{pkFeilds}, wkfdefStatus =#{wkfdefStatus},
modifyName =#{modifyName}, modifyTime =#{modifyTime}
where wkflwID=#{wkflwID}
