mapper.delete:
UPDATE WorkFlowLine
deleteName =#{deleteName}, deleteTime =#{deleteTime}, curStatus=0
WHERE fromNode=#{fromNode} OR toNode=#{toNode}

UPDATE WorkFlowLine
deleteName =#{deleteName}, deleteTime =#{deleteTime}, curStatus=0
WHERE pathId=#{pathId}

mapper.update:
UPDATE WorkFlowLine
pathTitle =#{pathTitle}, wkflwId =#{wkflwId}, fromNode =#{fromNode}, toNode =#{toNode}, isJunction =#{isJunction}, logUnitType =#{logUnitType}, logUnitParams =#{logUnitParams}, logTypeOfByMultActor =#{logTypeOfByMultActor}, isRebackPath =#{isRebackPath}, sqlFeild =#{sqlFeild}, judgeAfterAll =#{judgeAfterAll}, path1ToOtherTask =#{path1ToOtherTask}, isAsynchPath =#{isAsynchPath},
modifyName =#{modifyName}, modifyTime =#{modifyTime}
where pathId=#{pathId}

mapper.select:
SELECT * FROM WorkFlowLine

    and pathId in (${pathId})
    and pathTitle like concat('%', #{pathTitle}, '%')
    and wkflwId =#{wkflwId}
    and fromNode =#{fromNode}
    and toNode =#{toNode}

    and logUnitType =#{logUnitType}
    and logUnitParams =#{logUnitParams}

    and sqlFeild =#{sqlFeild}

    and curStatus=2

order by recordTime desc

SELECT count(*) FROM WorkFlowLine

    and pathId in (${pathId})
    and pathTitle like concat('%', #{pathTitle}, '%')
    and wkflwId =#{wkflwId}
    and fromNode =#{fromNode}
    and toNode =#{toNode}

    and logUnitType =#{logUnitType}
    and logUnitParams =#{logUnitParams}

    and sqlFeild =#{sqlFeild}

    and curStatus=2

order by recordTime desc
